var picturecount = 22;
var size = 3;
var picturepath = './content/img/screenshots/';
var extension = 'jpg';

var nextpicture = null;var pictures = new Array();$(function() {for (i=0;i<picturecount;i++) {current = pad(i, size);createBackgrounds(current);}changeBackground();});function pad(num, size) {var s = num+"";while (s.length < size) s = "0" + s;return s;}function createBackgrounds(num) {pictures[num] = new Image();pictures[num] = picturepath + num + '.' + extension;$('.background.container').append('<div class="background image'+num+'" style="background-image: url(\''+pictures[num]+'\'); opacity: 0;"></div>');}function changeBackground() {if(nextpicture != null) {$('.background.image'+pad(nextpicture, size)).animate({opacity: 0.0}, 750);}prevpicture = nextpicture;nextpicture = Math.round(Math.random()*(picturecount-1));if(nextpicture == prevpicture) {if(nextpicture < (picturecount-1)) {nextpicture += 1;} else if (nextpicture > 0) {nextpicture -= 1;}}$('.background.image'+pad(nextpicture, size)).animate({opacity: 1.0}, 750);setTimeout("changeBackground()", 10000);}